import React, {useEffect} from 'react';
import './SidebarOption.css';
import SpotifyWebApi from "spotify-web-api-js";
import { useStateValue } from '../utilities/StateProvider';

export default function SidebarOption({title, Icon, playlist_id}) {
    const [{token}, dispatch ] = useStateValue()
    const spotify = new SpotifyWebApi();
    spotify.setAccessToken(token);
    useEffect(() => {
        spotify.getPlaylistTracks('62ggkjMxZjTEuUsyB64ejH').then((track) =>{
            console.log('Track :', track)
        })
    
    }, [])

    const get_playlist_id = (playlist_id) =>{
        if(playlist_id){
            console.log
            spotify.getPlaylistTracks(playlist_id).then((track) =>{
                console.log('Track :', track)
            })
        }
         

    }
    return (
        <div className = 'sidebarOption'>
            {Icon && <Icon className = 'sidebarOption__icon'/>}
                {Icon? <h4>{title}</h4> : <p onClick={get_playlist_id}>{title}</p> }
              
          
            
        </div>
    )
}
