import React,{useState, useEffect, useRef, useP} from 'react';
import SpotifyPlayer from 'react-spotify-web-playback';
import { useStateValue } from '../utilities/StateProvider';
import './Player.css';
import axios from 'axios';

export default function Player() {
    const [{user,token, playlists, active_playlist, track, play}, dispatch] = useStateValue();
    
useEffect(() => {
    if(play = true){
        const prev__track = usePrevious(track)
        if(prev__track != track){
            const lyrics = axios.get('https://localhost:3001', {
                params:{
                    track: track.title,
                    artist: track.artist
                }
            }).then(res =>{console.log(res)})
        }
        
    }
}, [play,track])

function usePrevious (value) {
    const ref = useRef();
    useEffect(() => {
      ref.current = value;
    }, []);
    return ref.current
}
  
    function stop__play(){
        dispatch({
            type : 'SET_PLAY',
            payload: false
        
          })
    }
    return (
        <div className='footer'>
<SpotifyPlayer 
        token = {token? token:null}
        showSaveIcon
        callback={state =>{if(!state.isPlaying)stop__play()}}
        play={play?play:null}
        uris={track?[track.uri]:[]}
        name='MusicFest'
        autoPlay={true}
        persistDeviceSelection= {true}
        magnifySliderOnHover={true}
        styles={{
            activeColor: '#fff',
            bgColor: '#333',
            color: '#42f575',
            loaderColor: '#fff',
            sliderColor: '#1cb954',
            trackArtistColor: '#ccc',
            trackNameColor: '#fff',
          }}
       
        />
        </div>
        
    )
}
