import React,{} from 'react'
import './Lyrics.css'
import { useStateValue } from '../utilities/StateProvider';
import {Avatar} from '@material-ui/core';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';






export default function Lyrics() {
    const [{user,lyrics, track, play}, dispatch] = useStateValue(); 
   



    return (
        <div className='lyrics'>

        <div className="lyrics__header">
            
        <div>
    <ArrowBackIosIcon  className='home__arrow'/>
    <ArrowForwardIosIcon className='home__arrow'/>
</div>
<div>
<Avatar src={user?.images[0]?.url} alt='FO'/>
<h4>{user?.display_name}</h4>
</div> 

        </div>
        <div className="lyrics__body">
        {lyrics? (<div>
                <div className="lyrics__name">
                    {track?.name} - {track?.artists[0].name}
                </div>
                <div className='lyrics__text'> 
                {lyrics}

            </div>
            </div>): 
            (<div>Oops! Lyrics not found</div>)}
        </div>

            
        </div>
    )
}
