import React from 'react';
import './PlaylistCard.css';
import SpotifyWebApi from "spotify-web-api-js";


export default function PlaylistCard({title, details}) {
    const spotify = new SpotifyWebApi();
    const [{playlists,token,featured_playlists}, dispatch ] = useStateValue()

    useEffect(() => {
        if(token){
        spotify.setAccessToken(token);   
        }
      
        
    }, [playlists])
    const get_playlist_id = (playlist_id) =>{
        if(playlists){
            
            spotify.getPlaylist(playlist_id).then((res) => {
                dispatch({
                  type:'SET_ACTIVE_PLAYLIST',
                  payload: res
                })
              })
            
        }
         
    
    }
    

    return (
        <div className='card'>
            <img  width = '300'src='https://mosaic.scdn.co/640/ab67616d0000b2732dd4e7988b07d6fa6a09d6faab67616d0000b273568902770a5abaf05d1d459bab67616d0000b2735d2bd11398ee8d07900b6107ab67616d0000b273df831a49692a667a35664430' alt='plalist image'/>
            
            <h2>
               {title}
            </h2>
            <p>{details}</p>
        </div>
    )
}
