//sportify auth endpoint
export const authEndpoint = 'https://accounts.spotify.com/authorize';

const rediectUri = 'http://localhost:3000/';
const clientId = '7c94905bdafb43209c0f2b19bdcedb5e'
const scopes = [
    'user-read-currently-playing',
    'user-read-recently-played',
    'user-read-playback-state',
    'user-top-read',
    'user-modify-playback-state'
]

export const getTokenFromUrl = () =>{
    return window.location.hash.substring(1).split('&').reduce((initial, item) => {
        let parts = item.split('=');
        initial[parts]
    })
}

export const loginUrl = `${authEndpoint}?client_id=${clientId}&redirect_uri=${rediectUri}&scope=${scopes.join("%20")}&response_type=token&show_dialog=true`
