import React,{useState, useEffect} from 'react';
import './Search.css'
import SearchIcon from '@mui/icons-material/Search';
import {Avatar} from '@material-ui/core'
import { useStateValue } from '../utilities/StateProvider';
import SpotifyWebApi from "spotify-web-api-js";

const spotify = new SpotifyWebApi();
export default function Search() {
    const [{user,token}, dispatch] = useStateValue();
    const [search, setSearch] = useState('')
    const [search__result, setSearch__result] = useState([])


    useEffect(() => {
        if(token){
            spotify.setAccessToken(token); 
        }
    }, [token])

    useEffect(() => {
    if(!search){
        setSearch__result([])
    }
    if(search){
    
        spotify.searchTracks(search).then((res) =>{
             res.body.data.items.map(track =>{
                 //getting smallest image for albumUrl
                 const smallest__album__image = track.albumUrl.image.reduce((smallest, image) =>{
                     if(image.height < smallest.height) {
                         
                     }
                 })
                return {
                    artist: track.artists[0].name,
                    title: track.name,
                    uri : track.uri,
                    albumUrl: track.albumUrl.images
                }
             })
        })
    }
    }, [token, search])
    

    return (
        <div className= 'search'> 
            <div className="search__head">
                <div className="search__left">
                        <SearchIcon/>
                        <input placeholder= 'Search for Artists, Songs, or Albums' type = 'text' onChange={e =>{setSearch(e.target.value)}}/>
                </div>
            <   div className="search__right">
                        <Avatar src={user?.images[0]?.url} alt='FO'/>
                        <h4>{user?.display_name}</h4>
                </div> 
            </div>
            
            <div className="search__body">
                <h2>Search Results</h2>
            </div>
        </div>
    )
}
