import React,{useEffect, useState} from 'react';
import './Home.css'
import { useStateValue } from '../utilities/StateProvider';
import {Avatar} from '@material-ui/core';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import PlaylistCard from './PlaylistCard';
import SpotifyWebApi from "spotify-web-api-js";

export default function Home() {
  
    const spotify = new SpotifyWebApi();
    const [{user, playlists,token,featured_playlists}, dispatch ] = useStateValue();
    const [daily__mix, setDaily__mix] = useState()

    useEffect(() => {
        if(token){
        spotify.setAccessToken(token);  
        
        }

        spotify.getCategoryPlaylists('toplists').then((res) => {
            dispatch({
              type:'SET_FEATURED_PLAYLISTS',
              payload: res
            })
          }) 

          spotify.getPlaylist('37i9dQZF1E38IGLN7QKhuL').then((res) => {
            setDaily__mix(res)
            
          })
      
        
    }, [])

    const get_playlist_id = (playlist_id) =>{
        if(playlists){
            
            spotify.getPlaylist(playlist_id).then((res) => {
                dispatch({
                  type:'SET_ACTIVE_PLAYLIST',
                  payload: res
                })
              })
            
        }
         
    
    }
    

    return (
        <div className='home'>
            <div className="home__header">
    <div>
    <ArrowBackIosIcon  className='home__arrow'/>
    <ArrowForwardIosIcon className='home__arrow'/>
</div>
<div>
<Avatar src={user?.images[0]?.url} alt='FO'/>
<h4>{user?.display_name}</h4>
</div>

            </div>
            <div className="home__body">
                <div className="home__toplist">
                
                <div className="home__toplist__items">
                    <span>
                    <h2> Made For You</h2>
                    {daily__mix? <PlaylistCard name = {daily__mix.name} description={daily__mix.description} image__url = {daily__mix.images[0].url}/>:null}
                    </span>
                    <h2> Top-Hit Playlist</h2>
                    <span className='home__toplist__featured'>
                   
                    {featured_playlists?.playlists.items?.map((item) => 
                <PlaylistCard name = {item.name} description={item.description} image__url = {item.images[0].url}/>)}
                    </span>
                
                
                
                </div>
                </div>
                
            </div>
        </div>
    )
}
