import './Sidebar.css'
import SidebarOption from './SidebarOption';
import './SidebarOption.css';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import LibraryMusicIcon from '@mui/icons-material/LibraryMusic';
import { useStateValue } from '../utilities/StateProvider';

export default function Sidebar() {
const [{playlists}, dispatch ] = useStateValue()

useEffect(() => {
    
  console.log('',playlists)
}, [playlists])

    return (
        <div className= 'sidebar'>
            <img  className = 'sidebar_logo'src='https://storage.googleapis.com/pr-newsroom-wp/1/2018/11/Spotify_Logo_CMYK_Green.png' alt='sidebar logo'/>
            <SidebarOption title = 'Home' Icon = {HomeIcon}/>
            <SidebarOption title = 'Search' Icon = {SearchIcon}/>
            <SidebarOption title = 'Your Library' Icon = {LibraryMusicIcon}/>
            <br/>

            <strong className='sidebar__title'> PLAYLISTS </strong>
            <hr />
            {playlists?.items? playlists.map((playlist) => (
                <SidebarOption title = {playlists.name} /> 

            )): null}
           
        </div>

       
    )
}
