import React,{useEffect} from 'react'
import Body from './Body'
import Footer from './Footer'
import './Player.css'
import Sidebar from './Sidebar';
import { Link, Route, Switch } from 'react-router-dom';
import Home from './Home';
import UseAuth from './useAuth';
import SpotifyWebApi from "spotify-web-api-js";
import { useStateValue } from '../utilities/StateProvider';


const spotify = new SpotifyWebApi({
    clientId: '7c94905bdafb43209c0f2b19bdcedb5e'
  });

export default function Player({code, spotify}) {
    const [{user,token, playlists, active_playlist}, dispatch] = useStateValue();
    const accessToken = UseAuth(code)

    useEffect(() => {
        if(accessToken){
            dispatch({
              type: 'SET_TOKEN',
              payload : accessToken
            });
            //set access token for the spotiywebAPI
          spotify.setAccessToken(accessToken);
          //get user with spotifywebAPI
          spotify.getMe().then((user) => {console.log('ðŸ§”', user ); 
            dispatch({
            type: 'SET_USER',
            payload: user
          })})
        //get playlsits with spotifywebAPI
          spotify.getUserPlaylists().then ((playlists) => 
          dispatch({
            type : 'SET_PLAYLISTS',
            payload: playlists
        
          }))
            }
            console.log('spotify :', spotify)
        
    }, [accessToken])

        useEffect(() => {
            
            
        }, [token])
    return (
        <div className='player'>
            <div className='player_body'>
        {/*sidebar*/}
        <Sidebar/>

         {/*body*/}
         <Route exact={true} path="/" component={Home} />
                    <Route path="/body" component={() => <Body spotify = {spotify}/>} />
                    
         
            </div>
         
         {/*footer*/}
         <Footer/>
        </div>
    )
}
